package io.fpki.api.constants;

import io.fpki.api.pojo.CAEntry;

/**
 * This class produces a default initial CAEntry that serves at the Trust
 * Anchor, or, Root CA.
 * 
 * <pre>
 * {
 * 	"caAKI": "TRUST_ANCHOR_NOT_APPLICABLE",
 * 	"caCert": "",
 * 	"caCrl": "http://http.fpki.gov/fcpca/fcpca.crl",
 * 	"caHash": "894EBC0B23DA2A50C0186B7F8F25EF1F6B2935AF32A94584EF80AAF877A3A06E",
 * 	"caIssuer": "CN=Federal Common Policy CA,OU=FPKI,O=U.S. Government,C=US",
 * 	"caNotAfter": "2030-12-01T16:45:27.000+0000",
 * 	"caNotBefore": "2010-12-01T16:45:27.000+0000",
 * 	"caSerial": "0130",
 * 	"caSKI": "AD0C7A755CE5F398C479980EAC28FD97F4E702FC",
 * 	"caSubject": "CN=Federal Common Policy CA,OU=FPKI,O=U.S. Government,C=US"
 * }
 * </pre>
 */
public class TrustAnchor {

	private static String caAKI = "TRUST_ANCHOR_NOT_APPLICABLE";
	private static String caCert = "";
	private static String caCrl = "http://http.fpki.gov/fcpca/fcpca.crl";
	private static String caHash = "894EBC0B23DA2A50C0186B7F8F25EF1F6B2935AF32A94584EF80AAF877A3A06E";
	private static String caIssuer = "CN=Federal Common Policy CA,OU=FPKI,O=U.S. Government,C=US";
	private static String caNotAfter = "2030-12-01T16:45:27.000+0000";
	private static String caNotBefore = "2010-12-01T16:45:27.000+0000";
	private static String caSerial = "0130";
	private static String caSKI = "AD0C7A755CE5F398C479980EAC28FD97F4E702FC";
	private static String caSubject = "CN=Federal Common Policy CA,OU=FPKI,O=U.S. Government,C=US";

	/*
	 * Hidden Constructor
	 */
	private TrustAnchor() {
	}

	public static CAEntry getTrustAnchor() {
		return new CAEntry(caAKI, caCert, caCrl, caHash, caIssuer, caNotAfter, caNotBefore, caSerial, caSKI, caSubject);
	}

}
